package conversionesdetipos;

public class Conversionesdetipo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int e1, e2;
		e1 = 13;
		e2 = 72;
		double d1, d2;
		d1 = 13.69;
		d2 = 69.13;
		
		System.out.println("la variable e1 es: " + e1 + " la variable e2 es: " + e2 + " la variable d1 es: " + d1 + " la variable d2 es: " + d2); 
		
		System.out.println("la suma de las variables enteras es: " + (e1 + e2));
		
		System.out.println("la resta de las variables enteras e1 y e2: " + (e1 - e2));
		
		System.out.println("el producto de las variables enteras es: " + (e1 * e2));
		
		System.out.println("el cociente de las variables enteras es: " + (e2 / e1));
		
		System.out.println("el resto de la división anterior es: " + (e2 % e1));
		
		System.out.println("la suma de las variables double es: " + (d1 + d2));
		
		System.out.println("la sustracción de las variables double es: " + (d1 - d2));
		
		System.out.println("el producto de las variables double es: " + (d1 + d2));
		
		System.out.println("el producto de las variables double es: " + (d1 * d2));
		
		System.out.println("el producto de las variables double es: " + (d1 / d2));
		
		System.out.println("el doble de cada variable double es: e1: " + (e1 * 2) +" e2: " + (e2 * 2) + " d1: " + (d1 * 2) + " y d2: " + (d2 * 2));
		
		System.out.println("la suma de todas las variables es: " + (e1+ e2 + d1 + d2));
		
		System.out.println("el producto de todas las variables es: " + (e1 * e2 * d1 * d2));
		
		
	}

}
